/* eslint-env jest */
import RingCentral from '../src/ringcentral'
import dotenv from 'dotenv'

dotenv.config()

jest.setTimeout(64000)

const rc = new RingCentral(process.env.RINGCENTRAL_CLIENT_ID, process.env.RINGCENTRAL_CLIENT_SECRET, process.env.RINGCENTRAL_SERVER_URL)

describe('token issue', () => {
  test('default', async () => {
    rc.token({ 'id': '2125741008', 'token': { 'access_token': '', 'token_type': 'bearer', 'expires_in': 3600, 'refresh_token': '', 'refresh_token_expires_in': 604800, 'scope': 'EditReportingSettings EditMeetingsPresence Meetings EditLiveReports Voicemail VoipCalling ReadClientInfo SubscriptionAPNS Glip Interoperability DirectRingOut ReadContacts ReadAccounts SubscriptionGCM SubscriptionWebhook EditPaymentInfo EditPresence SendNotifications EditAccounts ReadMessages Faxes ReadPresence EditCallLog ReadCallRecording EditCustomData ReadLiveReports Contacts EditExtensions RoleManagement TelephonySessions RingOut NumberLookup SMS InternalMessages ReadCallLog Accounts EditMessages', 'owner_id': '2125741008', 'endpoint_id': 'TlNzMYc9RqOIO6mJkNT4BQ' } })

    try {
      await rc.get('/restapi/v1.0/account/~/extension/~')
    } catch (e) {
      expect(e.config.data).toEqual('grant_type=refresh_token&refresh_token=')
    }
  })
})
